import React from 'react'
import './about.css';
import AboutImg from '../../assets/about.jpg';
import CV from '../../assets/Cv.pdf';
import Info from './Info';

const About = () => {
  return (
    <section className="about section" id="about">
      <h2 className='section_title'>About Me</h2>
      <span className='section_subtitle'>My Introduction</span>

      <div className="about_container container grid">
        <img src={AboutImg} alt="" className="about_img" />

        <div className="about_data">
          <Info />

          <p className="about_description">I am a person who can handle all the responsibilities of a web application development process, including front-end and back-end development. I am proficient in various programming languages and frameworks such as JavaScript, HTML, CSS, React, Angular, Node.js, Express, and can work with databases like MySQL, MongoDB, etc. As a full stack developer i  have a solid understanding of web development concepts and can work on both client-side and server-side code to create robust and scalable web applications.</p>

          <a download='' href={CV} className="button button--flex">Download Cv</a>
        </div>
      </div>
    </section>
  )
}

export default About;
